SELECT 

FROM rui_bug_advice a

WHERE a.id = #{id}

		SELECT 

		,b.username,b.phone
		FROM rui_bug_advice a

		LEFT JOIN rui_user b ON a.user_id = b.id








                a.is_deal = #{isDeal}


				AND a.create_time >= #{createTime}


				AND a.create_time <= #{endTime}




				ORDER BY ${page.orderBy}

